catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	if (newValue.toString().toLowerCase() == 'true')
	{
		var companyFields = ['viking_automatic_sprinkler_company','delta_fire_systems_inc','international_fire_protection_inc','security_fire_protection','unites_states_alliance_fire_protection','davis_ulmer_sprinkler_co_inc','w_m_sprinkler_nyc_llc','american_fire_protection_group_inc'];
		var allCompanyField = 'dat_all_company_data';

		g_form.setValue(allCompanyField, 'false');
		//Check values of company fields to see if they're all true
		for (var i = 0; i < companyFields.length; i++){
			var fieldValue = g_form.getValue(companyFields[i]);
			//If any are unchecked then jump out of loop
			if (fieldValue.toString().toLowerCase() === 'false'){
				return;
			}
		}

		g_form.setValue(allCompanyField, 'true');
		//If we've gotten here, then all companies are marked true
		for (var j = 0; j < companyFields.length; j++){
			g_form.setValue(companyFields[j], 'false');
		}
	}
}
